// src/App.js
import React, { useState } from 'react';
import './App.css';

function App() {
  const [color1, setColor1] = useState('#ff0000');
  const [color2, setColor2] = useState('#0000ff');
  const [gradientType, setGradientType] = useState('linear');
  const [angle, setAngle] = useState(90);

  const handleColor1Change = (e) => setColor1(e.target.value);
  const handleColor2Change = (e) => setColor2(e.target.value);
  const handleGradientTypeChange = (e) => setGradientType(e.target.value);
  const handleAngleChange = (e) => setAngle(e.target.value);

  const getBackgroundStyle = () => {
    if (gradientType === 'linear') {
      return `linear-gradient(${angle}deg, ${color1}, ${color2})`;
    } else {
      return `radial-gradient(circle, ${color1}, ${color2})`;
    }
  };

  const getCSSCode = () => {
    if (gradientType === 'linear') {
      return `background: linear-gradient(${angle}deg, ${color1}, ${color2});`;
    } else {
      return `background: radial-gradient(circle, ${color1}, ${color2});`;
    }
  };

  return (
    <div className="App">
      <h1>Background Generator</h1>
      <div className="controls">
        <label>
          Color 1:
          <input type="color" value={color1} onChange={handleColor1Change} />
        </label>
        <label>
          Color 2:
          <input type="color" value={color2} onChange={handleColor2Change} />
        </label>
        <label>
          Gradient Type:
          <select value={gradientType} onChange={handleGradientTypeChange}>
            <option value="linear">Linear</option>
            <option value="radial">Radial</option>
          </select>
        </label>
        {gradientType === 'linear' && (
          <label>
            Angle:
            <input
              type="number"
              value={angle}
              onChange={handleAngleChange}
              min="0"
              max="360"
            />
          </label>
        )}
      </div>
      <div className="preview" style={{ background: getBackgroundStyle() }}>
        <h2>Preview</h2>
      </div>
      <div className="css-code">
        <h3>CSS Code</h3>
        <code>{getCSSCode()}</code>
      </div>
    </div>
  );
}

export default App;
